import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const PatientDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="container">
      <div className="dashboard">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <h1>Welcome, {user?.name}!</h1>
          <button className="btn btn-secondary" onClick={handleLogout}>
            Logout
          </button>
        </div>
        
        <p style={{ color: '#666', marginTop: '10px' }}>Email: {user?.email}</p>

        <div className="dashboard-grid">
          <div className="dashboard-card">
            <h3>üè• Book Appointment</h3>
            <p>Find and book doctors</p>
          </div>

          <div className="dashboard-card">
            <h3>üìã My Appointments</h3>
            <p>View your appointment status</p>
          </div>

          <div className="dashboard-card">
            <h3>üìç Nearby Doctors</h3>
            <p>Find doctors near you</p>
          </div>

          <div className="dashboard-card">
            <h3>üì∞ Health Blogs</h3>
            <p>Read health articles</p>
          </div>

          <div className="dashboard-card">
            <h3>üë§ Profile</h3>
            <p>Update your information</p>
          </div>
        </div>

        <div style={{ marginTop: '30px', padding: '20px', background: '#e8f5e9', borderRadius: '10px', border: '2px solid #4caf50' }}>
          <h3>üéâ Login Successful!</h3>
          <p>You have successfully logged into your patient account.</p>
          <p style={{ marginTop: '10px', color: '#666' }}>
            Welcome to DOCNEARYOU! You can now book appointments with doctors, view your medical history, and access health resources.
          </p>
        </div>
      </div>
    </div>
  );
};

export default PatientDashboard;
