// ============================================
// src/pages/DoctorRegister.js
// ============================================
// Simplified version using dummy face data for testing
// Replace with face-api.js version once models are working

import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { doctorAPI } from '../services/api';

const DoctorRegister = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: '',
    doctorId: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Validation
    if (!formData.name || !formData.doctorId || !formData.email || !formData.password) {
      setError('Please fill all fields');
      toast.error('Please fill all fields');
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      toast.error('Passwords do not match');
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters');
      toast.error('Password must be at least 6 characters');
      return;
    }

    setLoading(true);

    try {
      // Generate dummy face descriptor (128 random numbers between -1 and 1)
      // In production, this would come from face-api.js face detection
      const dummyFaceDescriptor = Array.from({ length: 128 }, () => Math.random() * 2 - 1);
      
      // Generate a tiny 1x1 transparent PNG as dummy face image
      const dummyFaceImage = 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNk+M9QDwADhgGAWjR9awAAAABJRU5ErkJggg==';

      console.log('Attempting doctor registration with:', {
        name: formData.name,
        doctorId: formData.doctorId,
        email: formData.email
      });

      const response = await doctorAPI.register({
        name: formData.name,
        doctorId: formData.doctorId,
        email: formData.email,
        password: formData.password,
        faceDescriptor: dummyFaceDescriptor,
        faceImage: dummyFaceImage
      });

      console.log('Doctor registration successful:', response.data);
      toast.success(response.data.message || 'Registration successful! Waiting for admin approval.');
      
      // Redirect to login page after 2 seconds
      setTimeout(() => {
        navigate('/doctor/login');
      }, 2000);
    } catch (err) {
      console.error('Doctor registration error:', err);
      console.error('Error response:', err.response);
      
      let errorMessage = 'Registration failed. Please try again.';
      
      if (err.response) {
        // Server responded with error
        errorMessage = err.response.data?.message || err.response.data?.error || errorMessage;
        console.error('Server error:', err.response.status, err.response.data);
      } else if (err.request) {
        // Request made but no response
        errorMessage = 'Cannot connect to server. Please ensure backend is running on port 5000.';
        console.error('No response from server');
      } else {
        // Error in request setup
        errorMessage = err.message;
      }
      
      setError(errorMessage);
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Doctor Registration</h2>
        
        {error && <div className="error-message">{error}</div>}

        {/* Testing Mode Warning */}
        <div style={{ 
          background: '#fff3cd', 
          padding: '15px', 
          borderRadius: '8px', 
          marginBottom: '20px',
          border: '1px solid #ffc107'
        }}>
          <p style={{ margin: 0, fontSize: '14px', color: '#856404' }}>
            ⚠️ <strong>Testing Mode:</strong> Using dummy face data for registration. Face recognition with camera will be implemented once face-api.js models are properly loaded.
          </p>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Full Name *</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Dr. John Doe"
              required
              autoComplete="name"
            />
          </div>

          <div className="form-group">
            <label>Doctor ID *</label>
            <input
              type="text"
              name="doctorId"
              value={formData.doctorId}
              onChange={handleChange}
              placeholder="DOC001"
              required
            />
            <small style={{ color: '#666', fontSize: '12px', display: 'block', marginTop: '5px' }}>
              Enter a unique doctor ID (e.g., DOC001, DOC002)
            </small>
          </div>

          <div className="form-group">
            <label>Email *</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="doctor@hospital.com"
              required
              autoComplete="email"
            />
          </div>

          <div className="form-group">
            <label>Password *</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Enter password (min 6 characters)"
              required
              minLength="6"
              autoComplete="new-password"
            />
          </div>

          <div className="form-group">
            <label>Confirm Password *</label>
            <input
              type="password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              placeholder="Re-enter password"
              required
              minLength="6"
              autoComplete="new-password"
            />
          </div>

          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={loading}
          >
            {loading ? 'Registering...' : 'Register'}
          </button>
        </form>

        <div className="link-text">
          Already have an account? <Link to="/doctor/login">Login here</Link>
        </div>
        <div className="link-text">
          <Link to="/">Back to Home</Link>
        </div>
      </div>
    </div>
  );
};

export default DoctorRegister;
