import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { chatbotAPI } from '../services/api';

const HealthChatbot = () => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState([
    {
      role: 'assistant',
      content: 'Hello! I\'m your DOCNEARYOU health assistant. I can help you with general health information and guide you on when to see a doctor. How can I assist you today?',
      timestamp: new Date()
    }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [commonQuestions, setCommonQuestions] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    fetchCommonQuestions();
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const fetchCommonQuestions = async () => {
    try {
      const response = await chatbotAPI.getCommonQuestions();
      setCommonQuestions(response.data.data);
    } catch (err) {
      console.error('Error fetching common questions:', err);
    }
  };

  const sendMessage = async (messageText = input) => {
    if (!messageText.trim()) return;

    const userMessage = {
      role: 'user',
      content: messageText,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setLoading(true);

    try {
      // Format conversation history for Gemini API
      // Skip the initial greeting message and only include actual conversation
      const conversationHistory = messages
        .slice(1) // Skip initial greeting
        .map(msg => ({
          role: msg.role === 'assistant' ? 'model' : 'user',
          parts: [{ text: msg.content }] // Gemini expects 'parts' array with 'text' field
        }));

      const response = await chatbotAPI.sendMessage(messageText, conversationHistory);
      
      const assistantMessage = {
        role: 'assistant',
        content: response.data.data.message,
        timestamp: new Date(),
        fallback: response.data.data.fallback
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (err) {
      console.error('Chat error:', err);
      toast.error('Failed to get response');
      const errorMessage = {
        role: 'assistant',
        content: 'I apologize, but I\'m having trouble connecting right now. Please try again later or contact a doctor directly.',
        timestamp: new Date(),
        error: true
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  const handleQuestionClick = (question) => {
    sendMessage(question);
  };

  return (
    <div className="container">
      <div className="dashboard">
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
          <h1>Health Assistant ChatBot!!</h1>
          <button className="btn btn-secondary" onClick={() => navigate('/patient/dashboard')}>
            Back
          </button>
        </div>

        <div style={{ background: '#fff3cd', padding: '15px', borderRadius: '10px', marginBottom: '20px', border: '2px solid #ffc107' }}>
          <p style={{ margin: 0, fontSize: '14px' }}>
            <strong>⚠️ Disclaimer:</strong> This is an AI assistant for general health information only. 
            For medical diagnosis and treatment, please consult with a qualified doctor.
          </p>
        </div>

        {commonQuestions.length > 0 && messages.length <= 1 && (
          <div style={{ marginBottom: '20px' }}>
            <h3>Common Questions:</h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '10px', marginTop: '10px' }}>
              {commonQuestions.map(q => (
                <button
                  key={q.id}
                  onClick={() => handleQuestionClick(q.question)}
                  style={{
                    padding: '12px',
                    background: '#f5f5f5',
                    border: '2px solid #e1e8ed',
                    borderRadius: '8px',
                    cursor: 'pointer',
                    textAlign: 'left',
                    fontSize: '14px',
                    transition: 'all 0.3s'
                  }}
                  onMouseOver={(e) => e.currentTarget.style.background = '#e3f2fd'}
                  onMouseOut={(e) => e.currentTarget.style.background = '#f5f5f5'}
                >
                  {q.question}
                </button>
              ))}
            </div>
          </div>
        )}

        <div style={{
          background: 'white',
          borderRadius: '10px',
          border: '2px solid #e1e8ed',
          height: '500px',
          display: 'flex',
          flexDirection: 'column'
        }}>
          <div style={{
            flex: 1,
            overflowY: 'auto',
            padding: '20px',
            display: 'flex',
            flexDirection: 'column',
            gap: '15px'
          }}>
            {messages.map((msg, idx) => (
              <div
                key={idx}
                style={{
                  display: 'flex',
                  justifyContent: msg.role === 'user' ? 'flex-end' : 'flex-start'
                }}
              >
                <div style={{
                  maxWidth: '70%',
                  padding: '12px 16px',
                  borderRadius: '15px',
                  background: msg.role === 'user' ? '#667eea' : msg.error ? '#ffebee' : '#f5f5f5',
                  color: msg.role === 'user' ? 'white' : '#333'
                }}>
                  <div style={{ whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}>
                    {msg.content}
                  </div>
                  <div style={{
                    fontSize: '11px',
                    marginTop: '5px',
                    opacity: 0.7
                  }}>
                    {msg.timestamp.toLocaleTimeString()}
                  </div>
                  {msg.fallback && (
                    <div style={{
                      fontSize: '11px',
                      marginTop: '5px',
                      fontStyle: 'italic',
                      opacity: 0.6
                    }}>
                      (Fallback response)
                    </div>
                  )}
                </div>
              </div>
            ))}
            {loading && (
              <div style={{ display: 'flex', justifyContent: 'flex-start' }}>
                <div style={{
                  padding: '12px 16px',
                  borderRadius: '15px',
                  background: '#f5f5f5'
                }}>
                  <span>Typing...</span>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          <div style={{
            padding: '15px',
            borderTop: '2px solid #e1e8ed',
            display: 'flex',
            gap: '10px'
          }}>
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
              placeholder="Ask me about your health concerns..."
              style={{
                flex: 1,
                padding: '12px',
                borderRadius: '25px',
                border: '2px solid #e1e8ed',
                fontSize: '14px'
              }}
              disabled={loading}
            />
            <button
              onClick={() => sendMessage()}
              disabled={loading || !input.trim()}
              style={{
                padding: '12px 24px',
                background: '#667eea',
                color: 'white',
                border: 'none',
                borderRadius: '25px',
                cursor: loading ? 'not-allowed' : 'pointer',
                fontWeight: 'bold',
                opacity: (loading || !input.trim()) ? 0.6 : 1
              }}
            >
              {loading ? '...' : 'Send'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HealthChatbot;
