import React, { useState, useEffect } from 'react';
import { BookOpen, Eye, Calendar, Tag, ArrowLeft, Search } from 'lucide-react';

const HealthBlogs = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [selectedBlog, setSelectedBlog] = useState(null);

  // Sample blog data - Replace with API call
  useEffect(() => {
    // Simulating API call
    setTimeout(() => {
      setBlogs([
        {
          id: 1,
          title: '5 Everyday Habits for a Healthier Heart',
          category: 'Cardiology',
          author: 'Dr. Amit Shah',
          authorSpecialization: 'Cardiologist',
          date: '2025-10-28',
          views: 1245,
          tags: ['heart health', 'lifestyle', 'wellness', 'prevention', 'doctor tips'],
          excerpt: 'Your heart works tirelessly, beating around 100,000 times a day to keep you alive. Yet, most of us don\'t give it the attention it truly deserves...',
          content: `Your heart works tirelessly, beating around 100,000 times a day to keep you alive. Yet, most of us don't give it the attention it truly deserves. Heart disease remains one of the leading causes of illness worldwide, but the good news is that small, consistent habits can make a big difference. Here are five doctor-recommended daily habits that can help you strengthen your heart and improve your overall well-being:

1. Move Every Day — Even a little movement goes a long way. Aim for at least 30 minutes of moderate activity like walking, cycling, or dancing. Regular exercise helps lower blood pressure, improve cholesterol levels, and maintain a healthy weight.

