  const categories = ['All', ...new Set(blogs.map(blog => blog.category))];

  const filteredBlogs = blogs.filter(blog => {
    const matchesSearch = blog.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         blog.excerpt.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         blog.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesCategory = selectedCategory === 'All' || blog.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('en-US', options);
  };

  if (selectedBlog) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50 p-6">
        <div className="max-w-4xl mx-auto">
          <button
            onClick={() => setSelectedBlog(null)}
            className="flex items-center gap-2 text-blue-600 hover:text-blue-700 mb-6 font-medium transition-colors"
          >
            <ArrowLeft size={20} />
            Back to Blogs
          </button>

          <article className="bg-white rounded-2xl shadow-lg overflow-hidden">
            <img
              src={selectedBlog.imageUrl}
              alt={selectedBlog.title}
              className="w-full h-80 object-cover"
            />
            
            <div className="p-8">
              <div className="flex items-center gap-4 mb-4">
                <span className="px-4 py-1.5 bg-blue-100 text-blue-700 rounded-full text-sm font-medium">
                  {selectedBlog.category}
                </span>
                <span className="flex items-center gap-1.5 text-gray-500 text-sm">
                  <Calendar size={16} />
                  {formatDate(selectedBlog.date)}
                </span>
                <span className="flex items-center gap-1.5 text-gray-500 text-sm">
                  <Eye size={16} />
                  {selectedBlog.views} views
                </span>
              </div>

              <h1 className="text-4xl font-bold text-gray-900 mb-4">
                {selectedBlog.title}
              </h1>

              <div className="flex items-center gap-3 mb-6 pb-6 border-b border-gray-200">
                <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-white font-semibold text-lg">
                  {selectedBlog.author.split(' ')[1][0]}
                </div>
                <div>
                  <p className="font-semibold text-gray-900">{selectedBlog.author}</p>
                  <p className="text-sm text-gray-600">{selectedBlog.authorSpecialization}</p>
                </div>
              </div>

              <div className="prose prose-lg max-w-none mb-6">
                {selectedBlog.content.split('\n\n').map((paragraph, idx) => (
                  <p key={idx} className="text-gray-700 leading-relaxed mb-4">
                    {paragraph}
                  </p>
                ))}
              </div>

              <div className="flex flex-wrap gap-2 pt-6 border-t border-gray-200">
                {selectedBlog.tags.map((tag, idx) => (
                  <span
                    key={idx}
                    className="px-3 py-1.5 bg-gray-100 text-gray-700 rounded-full text-sm flex items-center gap-1"
                  >
                    <Tag size={14} />
                    {tag}
                  </span>
                ))}
              </div>
            </div>
          </article>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50 p-6">
      <div className="max-w-7xl mx-auto">
        <div className="flex items-center gap-3 mb-8">
          <BookOpen size={36} className="text-green-600" />
          <h1 className="text-4xl font-bold text-gray-900">Health Blogs</h1>
        </div>

