import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { patientAPI } from '../services/api';

const NearbyDoctors = () => {
  const navigate = useNavigate();
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(false);
  const [userLocation, setUserLocation] = useState(null);
  const [selectedCluster, setSelectedCluster] = useState(null);

  useEffect(() => {
    getCurrentLocation();
  }, []);

  const getCurrentLocation = () => {
    if (!navigator.geolocation) {
      toast.error('Geolocation is not supported by your browser');
      return;
    }

    setLoading(true);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const location = {
          latitude: position.coords.latitude,
          longitude: position.coords.longitude
        };
        setUserLocation(location);
        fetchNearbyDoctors(location);
      },
      (error) => {
        console.error('Error getting location:', error);
        toast.error('Unable to get your location. Using default location.');
        // Default location (you can change this)
        const defaultLocation = { latitude: 25.5941, longitude: 85.1376 };
        setUserLocation(defaultLocation);
        fetchNearbyDoctors(defaultLocation);
      }
    );
  };

  const fetchNearbyDoctors = async (location) => {
    try {
      const response = await patientAPI.getNearbyDoctors(
        location.latitude,
        location.longitude,
        50000 // 50km radius
      );
      setDoctors(response.data.data);
      toast.success(`Found ${response.data.count} doctors nearby`);
    } catch (err) {
      toast.error('Failed to fetch nearby doctors');
    } finally {
      setLoading(false);
    }
  };

  const getClusterColor = (cluster) => {
    const colors = ['#4caf50', '#2196f3', '#ff9800'];
    return colors[cluster % 3];
  };

  const groupedDoctors = doctors.reduce((acc, doctor) => {
    const cluster = doctor.cluster || 0;
    if (!acc[cluster]) acc[cluster] = [];
    acc[cluster].push(doctor);
    return acc;
  }, {});

  if (loading) return <div className="loading">Finding doctors near you...</div>;

  return (
    <div className="container">
      <div className="dashboard">
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
          <h1>üìç Nearby Doctors</h1>
          <button className="btn btn-secondary" onClick={() => navigate('/patient/dashboard')}>
            Back
          </button>
        </div>

        {userLocation && (
          <div style={{ background: '#e3f2fd', padding: '15px', borderRadius: '10px', marginBottom: '20px' }}>
            <h3>Your Location</h3>
            <p>Latitude: {userLocation.latitude.toFixed(4)}, Longitude: {userLocation.longitude.toFixed(4)}</p>
            <p>Showing doctors within 50km radius</p>
          </div>
        )}

        <div style={{ marginBottom: '20px' }}>
          <h3>Doctors Grouped by Clusters (K-Means Algorithm)</h3>
          <p style={{ color: '#666' }}>Doctors are clustered based on their proximity to optimize your search</p>
        </div>

        <div style={{ display: 'flex', gap: '10px', marginBottom: '20px', flexWrap: 'wrap' }}>
          <button
            onClick={() => setSelectedCluster(null)}
            style={{
              padding: '8px 16px',
              background: selectedCluster === null ? '#667eea' : '#f5f5f5',
              color: selectedCluster === null ? 'white' : '#333',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer'
            }}
          >
            All Clusters
          </button>
          {Object.keys(groupedDoctors).map(cluster => (
            <button
              key={cluster}
              onClick={() => setSelectedCluster(parseInt(cluster))}
              style={{
                padding: '8px 16px',
                background: selectedCluster === parseInt(cluster) ? getClusterColor(cluster) : '#f5f5f5',
                color: selectedCluster === parseInt(cluster) ? 'white' : '#333',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
            >
              Cluster {parseInt(cluster) + 1} ({groupedDoctors[cluster].length})
            </button>
          ))}
        </div>

        {doctors.length === 0 ? (
          <div style={{ textAlign: 'center', padding: '40px', background: '#f5f5f5', borderRadius: '10px' }}>
            <h3>No doctors found nearby</h3>
            <p>Try expanding your search radius or check back later.</p>
          </div>
        ) : (
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '20px' }}>
            {doctors
              .filter(doc => selectedCluster === null || doc.cluster === selectedCluster)
              .map(doctor => (
                <div
                  key={doctor._id}
                  style={{
                    background: 'white',
                    padding: '20px',
                    borderRadius: '10px',
                    border: `3px solid ${getClusterColor(doctor.cluster)}`,
                    position: 'relative'
                  }}
                >
                  <div style={{
                    position: 'absolute',
                    top: '10px',
                    right: '10px',
                    padding: '5px 10px',
                    borderRadius: '20px',
                    background: getClusterColor(doctor.cluster),
                    color: 'white',
                    fontSize: '12px',
                    fontWeight: 'bold'
                  }}>
                    Cluster {doctor.cluster + 1}
                  </div>

                  <h3 style={{ color: '#667eea', marginTop: '10px' }}>{doctor.name}</h3>
                  <p><strong>Specialization:</strong> {doctor.specialization || 'General'}</p>
                  <p><strong>Hospital:</strong> {doctor.hospital || 'Not specified'}</p>
                  <p><strong>Distance:</strong> {(doctor.distance / 1000).toFixed(2)} km</p>
                  
                  {doctor.location?.address && (
                    <p><strong>Address:</strong> {doctor.location.address}</p>
                  )}

                  <button
                    onClick={() => navigate('/patient/book-appointment', { state: { doctor } })}
                    style={{
                      width: '100%',
                      marginTop: '15px',
                      padding: '10px',
                      background: '#667eea',
                      color: 'white',
                      border: 'none',
                      borderRadius: '5px',
                      cursor: 'pointer',
                      fontWeight: 'bold'
                    }}
                  >
                    Book Appointment
                  </button>
                </div>
              ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default NearbyDoctors;
