import React, { useState, useRef, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import * as faceapi from 'face-api.js';
import { doctorAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';

const MODEL_URL = '/models';

const DoctorLogin = () => {
  const navigate = useNavigate();
  const { login } = useAuth();

  const [formData, setFormData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [modelsLoaded, setModelsLoaded] = useState(false);
  const [cameraStarted, setCameraStarted] = useState(false);
  const [faceCaptured, setFaceCaptured] = useState(false);

  const videoRef = useRef(null);
  const streamRef = useRef(null);
  const descriptorRef = useRef(null);
  const faceImageRef = useRef(null);

  useEffect(() => {
    let cancelled = false;
    const load = async () => {
      try {
        await Promise.all([
          faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
          faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
          faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL)
        ]);
        if (!cancelled) setModelsLoaded(true);
      } catch (err) {
        console.error('Error loading face models:', err);
        toast.error('Failed to load face models. Check /models folder.');
      }
    };
    load();

    return () => { cancelled = true; stopCamera(); };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const startCamera = async () => {
    try {
      if (cameraStarted) return;
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      streamRef.current = stream;
      videoRef.current.srcObject = stream;
      await videoRef.current.play();
      setCameraStarted(true);
      setFaceCaptured(false);
      descriptorRef.current = null;
      faceImageRef.current = null;
    } catch (err) {
      console.error('Error starting camera:', err);
      toast.error('Unable to access camera. Allow camera permission and try again.');
    }
  };

  const stopCamera = () => {
    try {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(t => t.stop());
        streamRef.current = null;
      }
      if (videoRef.current) {
        videoRef.current.pause();
        videoRef.current.srcObject = null;
      }
      setCameraStarted(false);
    } catch (err) {
      console.error('Error stopping camera:', err);
    }
  };

  const captureFace = async () => {
    setError('');
    if (!modelsLoaded) {
      toast.error('Face models are still loading. Please wait a moment.');
      return;
    }
    if (!cameraStarted || !videoRef.current) {
      toast.error('Start the camera first.');
      return;
    }

    try {
      const detection = await faceapi
        .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions())
        .withFaceLandmarks()
        .withFaceDescriptor();

      if (!detection) {
        toast.error('No face detected. Ensure good lighting and that your face is centered.');
        return;
      }

      descriptorRef.current = Array.from(detection.descriptor);

      const canvas = document.createElement('canvas');
      canvas.width = 160;
      canvas.height = 160;
      const ctx = canvas.getContext('2d');
      ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
      faceImageRef.current = canvas.toDataURL('image/png');

      setFaceCaptured(true);
      toast.success('Face captured successfully.');
    } catch (err) {
      console.error('Error capturing face:', err);
      toast.error('Error capturing face. Try again.');
    }
  };

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!formData.email || !formData.password) {
      setError('Please fill all fields');
      toast.error('Please fill all fields');
      return;
    }

    if (!descriptorRef.current) {
      setError('Please capture your face before logging in');
      toast.error('Please capture your face before logging in');
      return;
    }

    setLoading(true);

    try {
      const payload = {
        email: formData.email,
        password: formData.password,
        faceDescriptor: descriptorRef.current,
        faceImage: faceImageRef.current // optional, backend may ignore for login
      };

      const response = await doctorAPI.login(payload);

      login(response.data.data, response.data.token, 'doctor');
      toast.success(response.data?.message || 'Login successful!');
      stopCamera();
      setTimeout(() => navigate('/doctor/dashboard'), 400);
    } catch (err) {
      console.error('Doctor login error:', err);
      let errorMessage = 'Login failed. Please try again.';
      if (err.response) {
        errorMessage = err.response.data?.message || err.response.data?.error || errorMessage;
      } else if (err.request) {
        errorMessage = 'Cannot connect to server. Please ensure backend is running on port 5000.';
      } else {
        errorMessage = err.message;
      }
      setError(errorMessage);
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Doctor Login</h2>

        {error && <div className="error-message">{error}</div>}

        <div style={{
          background: '#fff3cd',
          padding: '12px',
          borderRadius: '8px',
          marginBottom: '16px',
          border: '1px solid #ffc107'
        }}>
          <p style={{ margin: 0, fontSize: '14px', color: '#856404' }}>
            Testing Mode: Face capture enabled. Ensure models are present in <code>/models</code>.
          </p>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email Address *</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange}
              placeholder="doctor@hospital.com" required autoComplete="email" />
          </div>

          <div className="form-group">
            <label>Password *</label>
            <input type="password" name="password" value={formData.password} onChange={handleChange}
              placeholder="Enter your password" required autoComplete="current-password" />
          </div>

          <div style={{ marginTop: 12, marginBottom: 12 }}>
            <div style={{ display: 'flex', gap: 12, alignItems: 'flex-start' }}>
              <div>
                <video
                  ref={videoRef}
                  width={300}
                  height={250}
                  style={{ background: '#000', borderRadius: 6, display: 'block' }}
                  playsInline
                  muted
                />
                <div style={{ marginTop: 8 }}>
                  <button type="button" onClick={startCamera} className="btn btn-secondary" disabled={!modelsLoaded || cameraStarted}>
                    Start Camera
                  </button>
                  <button type="button" onClick={captureFace} className="btn btn-secondary" style={{ marginLeft: 8 }} disabled={!cameraStarted}>
                    Capture Face
                  </button>
                  <button type="button" onClick={stopCamera} className="btn btn-light" style={{ marginLeft: 8 }} disabled={!cameraStarted}>
                    Stop Camera
                  </button>
                </div>
              </div>

              <div style={{ flex: 1 }}>
                <div style={{ fontSize: 13, color: '#333' }}>
                  <strong>Face capture status:</strong>
                  <div>{modelsLoaded ? 'Models loaded' : 'Loading models...'}</div>
                  <div>{cameraStarted ? 'Camera started' : 'Camera stopped'}</div>
                  <div>{faceCaptured ? 'Face captured' : 'No face captured yet'}</div>
                </div>

                {faceImageRef.current && (
                  <div style={{ marginTop: 12 }}>
                    <label style={{ fontSize: 13 }}>Captured thumbnail:</label>
                    <div>
                      <img src={faceImageRef.current} alt="captured" width={120} height={120} style={{ borderRadius: 6, border: '1px solid #ddd' }} />
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>

          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>

        <div className="link-text" style={{ marginTop: 12 }}>
          Don't have an account? <Link to="/doctor/register">Register here</Link>
        </div>
        <div className="link-text" style={{ marginTop: 6 }}>
          <Link to="/">Back to Home</Link>
        </div>
      </div>
    </div>
  );
};

export default DoctorLogin;


